        body('Prepare_Where_Clauses')?['identification_clause'], 
        if(
                or(equals(body('Prepare_Where_Clauses')?['identification_clause'], string('')), equals(body('Prepare_Where_Clauses')?['identification_clause'], null)),
                string('WHERE S4SAccount__c = '),
                string(' AND S4SAccount__c = ')
        ), 
        string(''''), items('For_each_Account')?['Account']?['Id'], string(''''), 
        string(' LIMIT 200')
)}

